import React from "react";
import { Container, Row, Col } from "react-bootstrap"
import "./styles.css";
import { Typography } from "@mui/material";


class InfoPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: props.name,
            rotate: ["Compter Science", "Full-Stack Developer", "Software-Engineering"],
            currentWordIndex: 0,
        }
    }    

    componentDidMount() {
        this.timer = setInterval(() => {
          this.setState(prevState => ({
            currentWordIndex: (prevState.currentWordIndex + 1) % this.state.rotate.length,
          }));
        }, 500);
      }




    render() {
        const { rotate, currentWordIndex } = this.state;
        const currentWord = rotate[currentWordIndex];
        return (
            <section className="banner"> 
            <Container>
                <Row className="align-items-center">
                    <Col cs={12} md={6} xl={7}>
                        <span className="tagline"> Welcome to My Personal Website </span>
                        <Typography variant="h1">{`Hello! I'm Ashton Xiong`} <span className="wrapper "> {currentWord} </span></Typography>
                        <p> Description </p>
                    </Col>
                </Row>
            </Container>
            </section>
        )
    }
}

export default InfoPage;
